    String login = "VilmarDelfim";
    String senha = "senha123";

    private boolean logar(String validaLogin, String validaSenha) {

        char[] pass = campoSenha.getPassword();
        if (pass.length != validaSenha.length()) {

            return false;
        } else {
            if (!(validaLogin.equals(campoLogin.getText()))) {
                return false;
            } else {

                for (int i = 0; i < pass.length; i++) {

                    if (pass[i] != validaSenha.charAt(i)) {
                        return false;
                    }
                }

            }

            return true;

        }

    }

